ra Ann |editor2-last = Koos |editor2-first = Cheryl A. |title = The Human Tradition in Modern Europe, 1750 to the Present |publisher = Rowman & Littlefield |year = 2008 |url = https://books.google.com/books?id=9NK7mLsXN9IC&pg=PA75 |ref = harv}}
